mapper.update:
update product_master set category = #{category}, sub_category = #{subCategory}, brand = #{brand}, four_quadrant = #{fourQuadrant}, hs_code = #{hsCode}, hs_code_desc = #{hsCodeDesc}, status = #{status}, updated = current_timestamp, updated_by = #{updatedBy}
, approved = #{approved}, approved_by = #{approvedBy}
where sku = #{sku}

mapper.select:
select * from product_image
where sku = #{sku}
order by seq
